import { AuthData } from './../interfaces/authData';
import { StorageService } from './storage.service';
import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { BehaviorSubject, ReplaySubject, switchMap, tap } from 'rxjs';
import { Router } from '@angular/router';
import { environment } from '../../../environments/environment';
import {
  base64url,
  generateCodeChallenge,
  randomBytes,
} from '../../utils/utils';
import { CLIENT_ID, HEADER_CONFIG, SPORIFY_SCOPES } from '../../utils/config';

@Injectable({
  providedIn: 'root',
})
export class AuthService {
  private readonly authTokenUrl = 'https://accounts.spotify.com/api/token';
  private readonly authUrl = 'https://accounts.spotify.com/authorize';
  private readonly authDataSotrageKey = 'authData';
  token: string | undefined;
  authData = new BehaviorSubject<AuthData | undefined>(undefined);
  refreshToken = new BehaviorSubject<string | undefined>(undefined);
  loggedIn = new ReplaySubject<boolean>();

  constructor(
    private http: HttpClient,
    private router: Router,
    private storage: StorageService
  ) {
    const data: AuthData | null = this.storage.getData(this.authDataSotrageKey);
    if (data) {
      this.token = data.access_token;
      this.authData.next(data ?? undefined);
      this.refreshToken.next(data.refresh_token);
    }
    if (!data) this.loggedIn.next(false);

    this.syncAuthDataInStorage().subscribe();
  }

  /**
   * Auth data should be synced with local storage
   * over the life of the app
   * @returns Observable<AuthData | undefined>
   */
  syncAuthDataInStorage() {
    return this.authData.pipe(
      tap((authData) => {
        if (authData) this.storage.setData(this.authDataSotrageKey, authData);
      })
    );
  }

  logout() {
    this.storage.removeData(this.authDataSotrageKey);
    window.location.reload();
  }

  async login() {
    const code_verifier = base64url(randomBytes(96));
    const code = await generateCodeChallenge(code_verifier);
    const scope = SPORIFY_SCOPES.join('%20');
    const responseType = 'code';

    window.sessionStorage.setItem('code_verifier', code_verifier);
    window.location.href = `${this.authUrl}?client_id=${CLIENT_ID}&scope=${scope}&redirect_uri=${environment.redirectUri}&response_type=${responseType}&code_challenge_method=S256&code_challenge=${code}`;
  }

  getToken() {
    const code_verifier = window.sessionStorage.getItem('code_verifier') ?? '';
    const url = location.search;
    const code = new URLSearchParams(url).get('code') ?? '';
    const urlParams = new URLSearchParams();

    urlParams.append('grant_type', 'authorization_code');
    urlParams.append('code', code);
    urlParams.append('redirect_uri', environment.redirectUri);
    urlParams.append('client_id', CLIENT_ID);
    urlParams.append('code_verifier', code_verifier);

    return this.http
      .post<AuthData>(this.authTokenUrl, urlParams, HEADER_CONFIG)
      .pipe(
        tap((authData) => {
          this.token = authData.access_token;
          this.authData.next(authData);
          this.loggedIn.next(true);
          this.router.navigate(['']);
        })
      );
  }

  getRefreshToken() {
    return this.refreshToken.pipe(
      switchMap((refreshToken) => {
        const urlParams = new URLSearchParams({
          grant_type: 'refresh_token',
          refresh_token: refreshToken ?? '',
          client_id: CLIENT_ID,
        });

        return this.http.post<AuthData>(
          this.authTokenUrl,
          urlParams,
          HEADER_CONFIG
        );
      }),
      tap((authData) => {
        this.token = authData.access_token;
        this.authData.next(authData);
        this.loggedIn.next(true);
        // TODO: initialize script after making sure that token is valid
        // reload to properly initialize Spotify WebPlayer SDK
        window.location.reload();
      })
    );
  }
}

import { Queue } from './../interfaces/track';
import { Injectable, NgZone } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import {
  interval,
  distinctUntilKeyChanged,
  Subject,
  Subscription,
  tap,
  fromEvent,
} from 'rxjs';

@Injectable()
export class PlayerService {
  player?: Spotify.Player;
  playerState = new Subject<Spotify.PlaybackState>();
  playbackState = new Subject<number>();
  updateDestroy$!: Subscription;
  queue = new Subject<Queue>();
  deviceId = '';
  token!: string;

  constructor(private http: HttpClient, private _zone: NgZone) {
    this.queue.subscribe(({ queue, index }) => {
      this.playSong(queue, index);
    });
  }

  initializePlayer(token: string) {
    window.onSpotifyWebPlaybackSDKReady = () => {
      const player = new window.Spotify.Player({
        name: 'Web Playback SDK Quick Start Player',
        getOAuthToken: (cb: Function) => {
          cb(token);
        },
        volume: 0.5,
      });

      // Error handling
      player.addListener('initialization_error', ({ message }) => {
        console.error(message);
      });
      player.addListener('authentication_error', ({ message }) => {
        console.error(message);
      });
      player.addListener('account_error', ({ message }) => {});
      player.addListener('playback_error', ({ message }) => {
        console.error(message);
      });

      // Playback status updates
      const playerChangedEvent = fromEvent<Spotify.PlaybackState>(
        player,
        'player_state_changed'
      );
      playerChangedEvent.subscribe((newState) => {
        this._zone.run(() => {
          this.playerState.next(newState);
        });
      });

      playerChangedEvent
        .pipe(distinctUntilKeyChanged('paused'))
        .subscribe((newState) => {
          this._zone.run(() => {
            if (!newState.paused) {
              this.updateSlider();
            } else {
              this.destroyUpdateSubscriptions();
            }
          });
        });

      // Ready
      player.addListener('ready', ({ device_id }: { device_id: string }) => {
        ('ready');
        this.deviceId = device_id;
      });

      // Not Ready
      player.addListener(
        'not_ready',
        ({ device_id }: { device_id: string }) => {
          console.log('Device ID has gone offline', device_id);
        }
      );

      // Connect to the player!
      player.connect();
      this.player = player;
    };
  }

  getPlayerState() {
    return this.playerState.asObservable();
  }

  updateSlider() {
    this.updateDestroy$?.unsubscribe();
    this.updateDestroy$ = interval(300).subscribe(() => {
      this.player?.getCurrentState().then((state) => {
        const position = state?.position ?? 0;
        this.playbackState.next(position);
      });
    });
  }

  destroyUpdateSubscriptions() {
    this.updateDestroy$.unsubscribe();
  }

  playSong(uris: string[], index: number) {
    this.http
      .put(`me/player/play?device_id=${this.deviceId}`, {
        uris: uris,
        offset: {
          position: index,
        },
      })
      .pipe(
        tap(() => {
          this.playbackState.next(1);
        })
      )
      .subscribe();
  }

  tooglePlay() {
    this.player?.togglePlay().then(() => {});
  }

  skipNext() {
    this.destroyUpdateSubscriptions();
    this.playbackState.next(1);
    this.player?.nextTrack().then(() => {
      this.updateSlider();
    });
  }

  skipPrev() {
    this.destroyUpdateSubscriptions();
    this.playbackState.next(1);
    this.player?.previousTrack().then(() => {
      this.updateSlider();
    });
  }

  setVolume(volume: number) {
    this.player?.setVolume(volume).then(() => {});
  }

  setPosition(position: number) {
    this.player?.seek(position).then(() => {});
  }
}

import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root',
})
export class StorageService {
  getData(key: string) {
    const data = localStorage.getItem(key) ?? '';
    if (localStorage.getItem(key)) {
      return JSON.parse(data);
    } else {
      return '';
    }
  }

  setData(key: string, data: any): void {
    localStorage.setItem(key, JSON.stringify(data));
  }

  removeData(key: string) {
    localStorage.removeItem(key);
  }
}

import { createQuery } from '../../utils/createQuery';
import {
  FeaturedPlaylistResponse,
  Playlist,
  PlaylistResponse,
} from './../interfaces/playlist';
import { TracksResponse, SearchResponse, Track } from './../interfaces/track';
import { HttpClient, HttpParams } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root',
})
export class TrackService {
  private params = new HttpParams();
  readonly pageSize = 6;

  constructor(private http: HttpClient) {
    this.params = this.params.append('limit', this.pageSize);
  }

  getSavedTracks(offset: number) {
    const params = this.params.append('offset', offset);
    return createQuery(
      ['savedTracks', { page: offset }] as const,
      this.http.get<TracksResponse>(`me/tracks`, { params })
    );
  }

  getPlaylistTracks(id: string, offset: number) {
    const params = this.params.append('offset', offset);
    return createQuery(
      ['playlistTracks', { id, offset }] as const,
      this.http.get<TracksResponse>(`playlists/${id}/tracks`, { params })
    );
  }

  getTopTracks(offset: number) {
    const params = this.params.append('offset', offset);
    return createQuery(
      ['topTracks', { offset }] as const,
      this.http.get<TracksResponse<Track>>(`me/top/tracks`, { params })
    );
  }

  getUserPlaylists() {
    return createQuery(
      ['userPlaylists'],
      this.http.get<PlaylistResponse>(`me/playlists`)
    );
  }

  getFeaturedPlaylists() {
    const params = this.params.set('limit', 12);
    return createQuery(
      ['featuredPlaylists'] as const,
      this.http.get<FeaturedPlaylistResponse>(`browse/featured-playlists`, {
        params,
      })
    );
  }

  getPlaylistInfo(id: string) {
    return createQuery(
      ['playlistInfo', { id }] as const,
      this.http.get<Playlist>(`playlists/${id}`)
    );
  }

  getSearchResults(query: string) {
    return createQuery(
      ['searchResults', { query }] as const,
      this.http.get<SearchResponse>(`search`, {
        params: { q: query, type: ['track'], limit: 10 },
      }),
      !!query
    );
  }

  checkUserSavedTracks(trackIds: string[]) {
    return createQuery(
      ['isSavedTrack', { trackIds }] as const,
      this.http.get<boolean[]>(`me/tracks/contains`, {
        params: {
          ids: trackIds,
        },
      }),
      !!trackIds.length
    );
  }

  saveTrack(trackId: string) {
    return this.http.put<void>(`me/tracks`, { ids: [trackId] });
  }

  deleteTrack(trackId: string) {
    return this.http.delete<void>(`me/tracks`, {
      body: {
        ids: [trackId],
      },
    });
  }

  toggleShuffle(state: boolean) {
    return this.http.put<void>(`me/player/shuffle?state=${state}`, {});
  }
}

import { AuthService } from './auth.service';
import { User } from './../interfaces/user';
import { HttpClient } from '@angular/common/http';
import { Injectable, inject } from '@angular/core';
import { createQuery } from '../../utils/createQuery';
import { tap } from 'rxjs';

@Injectable({
  providedIn: 'root',
})
export class UserService {
  private http = inject(HttpClient);
  private authService = inject(AuthService);

  getUserData() {
    return createQuery(
      ['userData'] as const,
      this.http.get<User>(`me`).pipe(
        tap(() => {
          this.authService.loggedIn.next(true);
        })
      )
    );
  }
}

