import { Injectable, inject } from '@angular/core';
import {
  HttpRequest,
  HttpHandler,
  HttpEvent,
  HttpInterceptor,
  HttpErrorResponse,
} from '@angular/common/http';
import { catchError, Observable, switchMap, take, throwError } from 'rxjs';
import { AuthService } from '../services/auth.service';
import { BASE_URL } from '../../utils/config';

@Injectable()
export class AuthInterceptor implements HttpInterceptor {
  private authService = inject(AuthService);

  intercept(
    req: HttpRequest<unknown>,
    next: HttpHandler
  ): Observable<HttpEvent<unknown>> {
    const token = this.authService.token;
    if (token) {
      let modifiedReq = req;
      if (!req.url.includes('/token')) {
        // add authorization header to request if we not requesting /token path
        modifiedReq = req.clone({
          headers: req.headers.set('Authorization', `Bearer ${token}`),
          url: `${BASE_URL}${req.url}`,
        });
      }

      return next.handle(modifiedReq).pipe(
        catchError((error: HttpErrorResponse) => {
          // refresh token if 401 error
          if (error.status === 401)
            return this.handle401Error(modifiedReq, next)(error);

          // if other error logout user
          this.authService.logout();
          return throwError(() => error);
        })
      );
    }

    return next.handle(req).pipe(
      catchError((err) => {
        return throwError(() => err);
      })
    );
  }

  handle401Error(req: HttpRequest<unknown>, next: HttpHandler) {
    return (error: any) => {
      if (req.url.includes('/token')) {
        this.authService.logout();
        return throwError(() => error);
      }

      return this.authService.getRefreshToken().pipe(
        switchMap((authData) => {
          const modifiedReq = req.clone({
            headers: req.headers.set(
              'Authorization',
              `Bearer ${authData.access_token}`
            ),
            url: req.url,
          });
          return next.handle(modifiedReq);
        }),
        catchError((error) => {
          this.authService.logout();
          return throwError(() => error);
        })
      );
    };
  }
}

import {
  HttpHandler,
  HttpHeaderResponse,
  HttpInterceptor,
  HttpProgressEvent,
  HttpRequest,
  HttpResponse,
  HttpSentEvent,
  HttpUserEvent,
} from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable, delay } from 'rxjs';
import { User } from '../app/shared/interfaces/user';
import {
  FeaturedPlaylistResponse,
  Playlist,
  PlaylistResponse,
} from '../app/shared/interfaces/playlist';
import {
  featuredPlaylists,
  playlist,
  playlistTracks,
  playlistsResponse,
  savedTracks,
  searchResponse,
  user,
} from './mocks';
import {
  SearchResponse,
  Track,
  TracksResponse,
} from 'src/app/shared/interfaces/track';

@Injectable()
export class MockBackendInterceptor implements HttpInterceptor {
  intercept(
    req: HttpRequest<any>,
    next: HttpHandler
  ): Observable<
    | HttpSentEvent
    | HttpHeaderResponse
    | HttpProgressEvent
    | HttpResponse<any>
    | HttpUserEvent<any>
  > {
    if (req.url.endsWith('me') && req.method == 'GET') {
      return new Observable<HttpResponse<User>>((observer) => {
        observer.next(
          new HttpResponse<User>({
            body: user,
            status: 200,
          })
        );
        observer.complete();
      }).pipe(delay(200));
    }

    if (req.url.endsWith('playlists') && req.method == 'GET') {
      return new Observable<HttpResponse<PlaylistResponse>>((observer) => {
        observer.next(
          new HttpResponse<PlaylistResponse>({
            body: playlistsResponse,
            status: 200,
          })
        );
        observer.complete();
      }).pipe(delay(200));
    }

    if (
      req.url.endsWith('playlists/50Gsv3p7qLLPVzfPBu8UcO') &&
      req.method == 'GET'
    ) {
      return new Observable<HttpResponse<Playlist>>((observer) => {
        observer.next(
          new HttpResponse<Playlist>({
            body: playlist,
            status: 200,
          })
        );
        observer.complete();
      }).pipe(delay(200));
    }

    if (req.url.endsWith('browse/featured-playlists') && req.method == 'GET') {
      return new Observable<HttpResponse<FeaturedPlaylistResponse>>(
        (observer) => {
          observer.next(
            new HttpResponse<FeaturedPlaylistResponse>({
              body: featuredPlaylists,
              status: 200,
            })
          );
          observer.complete();
        }
      ).pipe(delay(200));
    }

    if (req.url.endsWith('me/tracks') && req.method == 'GET') {
      return new Observable((observer) => {
        observer.next(
          new HttpResponse<TracksResponse>({
            body: savedTracks,
            status: 200,
          })
        );
        observer.complete();
      });
    }

    if (req.url.endsWith('search') && req.method == 'GET') {
      return new Observable<HttpResponse<SearchResponse>>((observer) => {
        observer.next(
          new HttpResponse<SearchResponse>({
            body: searchResponse,
            status: 200,
          })
        );
        observer.complete();
      }).pipe(delay(200));
    }

    if (req.url.match(/playlists\/.*\/tracks/)) {
      return new Observable<HttpResponse<TracksResponse>>((observer) => {
        observer.next(
          new HttpResponse<TracksResponse>({
            body: playlistTracks,
            status: 200,
          })
        );
        observer.complete();
      }).pipe(delay(200));
    }

    if (req.url.endsWith('me/top/tracks')) {
      return new Observable<HttpResponse<TracksResponse<Track>>>((observer) => {
        observer.next(
          new HttpResponse<TracksResponse<Track>>({
            body: {
              ...savedTracks,
              items: savedTracks.items.map((item) => ({
                ...item.track,
              })),
            },
            status: 200,
          })
        );
        observer.complete();
      }).pipe(delay(200));
    }
    // pass through other requests.
    return next.handle(req);
  }
}

